import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useSearchParams } from 'react-router-dom';
import Header from '../components/header';
import { useAuth0 } from '@auth0/auth0-react';
import { useNavigate } from 'react-router-dom';

function WeatherScreen() {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const city = searchParams.get('city');
  const { isAuthenticated, logout } = useAuth0();
  const [data, setData] = useState({});

  if (!isAuthenticated) {
    navigate('/');
  }

  const fetchingWeather = () => {
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&cnt=1&appid=${process.env.REACT_APP_API_KEY}`
      )
      .then(function (response) {
        // handle success
        setData(response.data);
        console.log(response.data);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        // always executed
      });
  };

  useEffect(() => {
    fetchingWeather();
  }, []);

  return (
    <>
      <Header>
        <button
          onClick={() => {
            logout({
              logoutParams: {
                returnTo: window.location.origin,
              },
            });
          }}
        >
          Log out
        </button>
      </Header>
      <h1>weather screen</h1>

      {data.list && data.list.length > 0 && (
        <div className="container mx-auto mt-8">
          <table className="table-auto">
            <thead>
              <tr>
                <th className="px-4 py-2">Temperature</th>
                <th className="px-4 py-2">City</th>
                <th className="px-4 py-2">Country</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="border px-4 py-2">{data.list[0].main.temp}</td>
                {data.city ? (
                  <>
                    <td className="border px-4 py-2">{data.city.name}</td>
                    <td className="border px-4 py-2">{data.city.country}</td>
                  </>
                ) : (
                  <td className="border px-4 py-2" colSpan="2">
                    City information not available
                  </td>
                )}
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </>
  );
}

export default WeatherScreen;
