import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/header';

function LandingScreen() {
  const navigate = useNavigate();
  const { isAuthenticated, loginWithRedirect } = useAuth0();

  if (isAuthenticated) {
    navigate('/home');
  }
  return (
    <>
      <Header />
      <div className="flex flex-col items-center justify-center">
        <p className="text-center mb-8">
          Welcome to the weather forecast web application. Please login with
          your <br />
          Github user to use the application and view the weather in your city.
        </p>
        <button
          onClick={loginWithRedirect}
          className="bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 text-white font-medium rounded-lg py-2 px-4"
        >
          Log in with Github
        </button>
      </div>
    </>
  );
}

export default LandingScreen;
